#define WX_THIRD_HTML @"<!doctype html><html><script type=\"text/javascript\"src=\"./js/lib/ABI.js\"></script><script type=\"text/javascript\"src=\"./js/lib/ethereumjs-all.js\"></script><script type=\"text/javascript\"src=\"./js/lib/wzrdin.js\"></script><script type=\"text/javascript\"src=\"./js/lib/keythereum.min.js\"></script><script type=\"text/javascript\"src=\"./js/lib/lightwallet.min.js\"></script><script type=\"text/javascript\"src=\"./js/lib/web3js0.16.0.js\"></script><script type=\"text/javascript\"src=\"./js/lib/ethers-wallet.min.js\"></script><script type=\"text/javascript\"src=\"./js/wrap/transactionSender.js\"></script><script type=\"text/javascript\"src=\"./js/wrap/contractManager.js\"></script><script type=\"text/javascript\"src=\"./js/wrap/walletManager.js\"></script><head><meta name=\"viewport\"content=\"user-scalable=no,width=device-width,initial-scale=1.0,maximum-scale=1.0\"></head><body><script>var myWeb3Provider;function setupWebViewJavascriptBridge(callback){if(window.WebViewJavascriptBridge){return callback(WebViewJavascriptBridge)}if(window.WVJBCallbacks){return window.WVJBCallbacks.push(callback)}window.WVJBCallbacks=[callback];var WVJBIframe=document.createElement('iframe');WVJBIframe.style.display='none';WVJBIframe.src='https://__bridge_loaded__';document.documentElement.appendChild(WVJBIframe);setTimeout(function(){document.documentElement.removeChild(WVJBIframe)},0)}setupWebViewJavascriptBridge(function(bridge){var uniqueId=1;function log(message,data){var log=document.getElementById('log');var el=document.createElement('div');el.className='logLine';el.innerHTML=uniqueId++ +'. '+message+':<br/>'+JSON.stringify(data);if(log.children.length){log.insertBefore(el,log.children[0])}else{log.appendChild(el)}};bridge.registerHandler('ethInitProvider',function(data,responseCallback){try{myWeb3Provider=new window.Web3.providers.HttpProvider(data);responseCallback(myWeb3Provider)}catch(err){ responseCallback(err); }});bridge.registerHandler('ethQueryTransaction',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var txInfo=web3.eth.getTransactionReceipt(data);responseCallback(txInfo)});bridge.registerHandler('ethSendRawTransaction',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var hash=web3.eth.sendRawTransaction(data);responseCallback(hash)});bridge.registerHandler('ethCreatePK',function(data,responseCallback){var keythereumObj=window.keythereum;var params={keyBytes:32,ivBytes:16};var dk=keythereumObj.create(params);var privateKeyBuf=window.buffer.Buffer(dk.privateKey);var pk=privateKeyBuf.toString(\"hex\",0);responseCallback(pk)});bridge.registerHandler('ethCreateKeyStore',function(data,responseCallback){var keythereumObj=window.keythereum;var params={keyBytes:32,ivBytes:16};var dk=keythereumObj.create(params);var n=65536;var options={kdf:\"scrypt\",cipher:\"aes-128-ctr\",kdfparams:{n:n,p:1,r:8,dklen:32,prf:\"hmac-sha256\"}};var pk=data[\"pk\"];var password=data[\"pwd\"];var result;try{result=keythereumObj.dump(password,pk,dk.salt,dk.iv,options)}catch(err){var exception={ethException:\"ethException\",message:err.message};responseCallback(exception);return}result[\"address\"]=\"0x\"+result[\"address\"];responseCallback(result)});bridge.registerHandler('ethRecoverPrivateKey',function(data,responseCallback){var keythereumObj=window.keythereum;var jsonStore=JSON.parse(data[\"keyVal\"]);var password=data[\"pwd\"];var keyGen;try{keyGen=keythereum.recover(password,jsonStore)}catch(err){var exception={ethException:\"ethException\",message:err.message};responseCallback(exception);return}var privateKeyBuf2=window.buffer.Buffer(keyGen);var result=privateKeyBuf2.toString(\"hex\",0);responseCallback(result)});bridge.registerHandler('ethSignTransaction',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var pk=data[\"pk\"];var v_to=data[\"to\"];var v_data=data[\"data\"];var nonce=web3.nonce();var rawTx={gasPrice:21000000000,gasLimit:200000000,value:0,nonce:nonce,to:v_to,data:v_data};TransactionSender._nonce=nonce+1;var Tx=EthJS.Tx;var tx=new Tx(rawTx);var Buffer=window.buffer.Buffer;var privateKey=new Buffer(pk,'hex');tx.sign(privateKey);var serializedTx=tx.serialize();var serializedTxHex=\"0x\"+serializedTx.toString('hex');responseCallback(serializedTxHex)})});</script></body></html>";

#define WX_NORMAL_HTML @"<!doctype html><html><head><script type=\"text/javascript\"src=\"./js10/lib/web3.min.js\"></script><script src='./js10/lib/bignumber.js'></script><script src='./js10/lib/crunch.js'></script><meta name=\"viewport\"content=\"user-scalable=no, width=device-width, initial-scale=1.0, maximum-scale=1.0\"></head><body><script>window.onerror=function(err){log('window.onerror: '+err)};var myWeb3Provider;function getNonce(nonceVal){var timestamp=new Date().getTime();for(var i=7;i>=0;i--){nonce[i]=timestamp%256;timestamp/=256}return nonce}function setupWebViewJavascriptBridge(callback){if(window.WebViewJavascriptBridge){return callback(WebViewJavascriptBridge)}if(window.WVJBCallbacks){return window.WVJBCallbacks.push(callback)}window.WVJBCallbacks=[callback];var WVJBIframe=document.createElement('iframe');WVJBIframe.style.display='none';WVJBIframe.src='https://__bridge_loaded__';document.documentElement.appendChild(WVJBIframe);setTimeout(function(){document.documentElement.removeChild(WVJBIframe)},0)}setupWebViewJavascriptBridge(function(bridge){bridge.registerHandler('ethInitProvider',function(data,responseCallback){bridge.registerHandler('ethHexFromBytes',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var hexVal=eval(\"(\"+data+\")\");var hexVal=web3.utils.bytesToHex(hexVal);responseCallback(hexVal)});myWeb3Provider=new window.Web3.providers.HttpProvider(data);responseCallback(myWeb3Provider)});bridge.registerHandler('ethKeystoreEnc',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var pk=data[\"pk\"];var pwd=data[\"pwd\"];var keyVal=web3.eth.accounts.encrypt(pk,pwd);responseCallback(keyVal)});bridge.registerHandler('ethKeystoreDec',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var keyVal=data[\"keyVal\"];var pwd=data[\"pwd\"];var account=web3.eth.accounts.decrypt(keyVal,pwd);responseCallback(account)});bridge.registerHandler('accountsCreate',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var account=web3.eth.accounts.create();responseCallback(account)});bridge.registerHandler('accountFromPrivateKey',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var account=web3.eth.accounts.privateKeyToAccount(data);responseCallback(account)});bridge.registerHandler('ethNonce',function(data,responseCallback){var nonce=getNonce(data);responseCallback(nonce)});bridge.registerHandler('ethSha3',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var shaVal=web3.utils.sha3(data);responseCallback(shaVal)});bridge.registerHandler('bigValue',function(data,responseCallback){var x=new BigNumber(data);responseCallback(x.toFixed(0))});bridge.registerHandler('crunchValue',function(data,responseCallback){var crunch=Crunch();var byteList=eval(\"(\"+data+\")\");var valNumber=crunch.stringify(byteList);responseCallback(valNumber)});bridge.registerHandler('ethFunCall',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var to=data[\"to\"];var encData=data[\"data\"];web3.eth.call({to:to,data:encData}).then(function(value){responseCallback(value)})});bridge.registerHandler('ethEncodeFunCall',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var abiJson=data[\"abiInterface\"];var abiJson=eval(\"(\"+abiJson+\")\");var encodeValue;var params=data[\"params\"];if(params!=null){params=eval(\"(\"+params+\")\");encodeValue=web3.eth.abi.encodeFunctionCall(abiJson,params)}else{encodeValue=web3.eth.abi.encodeFunctionCall(abiJson)}responseCallback(encodeValue)});bridge.registerHandler('ethSignTransaction',function(data,responseCallback){var web3=new window.Web3(myWeb3Provider);var pk=data[\"pk\"];var v_to=data[\"to\"];var v_val=data[\"value\"];var v_gas=data[\"gas\"];var v_gasPrice=data[\"gasPrice\"];var v_data=data[\"data\"];var v_nonce=data[\"nonce\"];var jsonVal=JSON.stringify({to:v_to,value:v_val,gas:v_gas,gasPrice:v_gasPrice,nonce:v_nonce,data:v_data,chainId:0});var sign=web3.eth.accounts.signTransaction({to:v_to,value:v_val,gas:v_gas,gasPrice:v_gasPrice,nonce:v_nonce,data:v_data},pk).then(function(value){responseCallback(value)})})});</script><div id='buttons'></div><div id='log'></div></body></html>"
